		['smithcraft'] = {
			['1'] = [[Di kamp para korban selamat, perhatianku tertuju pada seorang pria paruh baya berwajah pucat yang duduk di kursi kayu. Dia sedang mencuci pakaian di baskom timah di depannya.]],
			['3'] = [[Aku sudah mengumpulkan semua bahan untuk membangun bengkel pandai besi. Akan kuberi tahu Ignat, dia pasti senang sekali<new_line><new_line>- Bawa semua bahan ke Ignat]],
			['2'] = [[Aku membuat kesepakatan dengan Ignat dari kamp Tula. Aku akan membangun bengkel pandai besi untuknya dan, sebagai gantinya, dia akan mengajariku pandai besi. Aku harus mencari bahan yang kami butuhkan dan membawanya kamp.<new_line><new_line>(Kamu bisa mendapatkan bata tahan api dengan membongkar oven di rumah kayu)]],
			['5'] = [[Ignat dan aku sudah membangun bengkel pandai besi yang sangat bagus! Semoga ini akan membuatnya bahagia sekarang...]],
			['4'] = [[Sekarang adalah waktu yang tepat untuk pergi ke gudang tua dan membangun bengkel pandai besi!]],
			['name'] = [[Pandai besi]]
		},
		['advice_trauma'] = {
			['1'] = [[Pereda nyeri memungkinkan kamu mengabaikan efek luka-luka selama 24 jam. Jika tulang retak membuatmu tidak bisa membawa barang, konsumsi saja pereda nyeri!]],
			['hint'] = [[Dapatkan efek pereda nyeri]],
			['name'] = [[Tips: Pereda nyeri]]
		},
		['death_recovery'] = {
			['1'] = [[Aku tak tahu bagaimana melakukannya, tetapi aku bertahan dan berhasil sampai ke tempat perlindungan. Aku rasa aku meninggalkan beberapa barangku saat aku pingsan. Aku harus kembali dan mendapatkannya.]],
			['3'] = [[Aku berhasil mendapatkan kembali sedikitnya beberapa barang yang telah dicuri. Penjarah berhasil membawa sisa barangku ke gudang mereka di dekat sini. Aku menemukan peta tempat itu di salah satu tubuh mereka. Gudang itu kemungkinan dijaga ketat, jadi aku harus siap untuk bertarung.]],
			['2'] = [[Seperti yang diduga, penjarah sudah ada di sini dan mengambil hampir semuanya. Untungnya, mereka meninggalkan jejak dan belum begitu jauh, jadi tanpa kesulitan aku akan menemukan mereka.]],
			['4'] = [[Aku berhasil mengalahkan penjarah dan mendapatkan barangku kembali.]],
			['name'] = [[Barang Ditemukan]],
			['hint'] = [[Jangan lupa mengambil barangku yang ditemukan kembali.]]
		},
		['common_complite'] = [[Aku sudah menemukan semua yang kuperlukan. Saatnya kembali dan mengambil hadiahku.]],
		['belaz'] = {
			['1'] = [[Aku diberi tahu tentang seorang pria yang mencari asisten untuk memperbaiki truk tambang. Aku harus tahu selengkapnya.]],
			['name'] = [[Kota bergerak]],
			['3'] = [[Selesai.<new_line>Pemilik truk tambang, Vladimir, sangat berterima kasih atas bantuanku... Mimpinya akhirnya menjadi kenyataan. Dia setuju untuk mengizinkanku pergi bersama dia dan keluarganya ke mana pun aku mau. Dia ingin menguji kota bergerak miliknya. Sekarang aku harus memutuskan mau pergi ke mana...]],
			['2'] = [[Orang ini sudah gila. Dia mau menanamkan reaktor atom ke truk tambangnya! Dia memberikanku daftar suku cadang dan peta. Sepertinya aku juga sudah gila...]]
		},
		['advice_radiation'] = {
			['1'] = [[Radiasi yang menumpuk tidak akan hilang begitu saja. Level radiasi yang tinggi meningkatkan risiko penyakit radiasi!<new_line>Radiasi berkurang dengan beberapa obat, alkohol, dan air bersih. Teh Dandelion, chanterelle dari hutan, dan kranberi dari rawa-rawa juga membantu melawan radiasi.]],
